  }
});
Vue.prototype.API = API;
console.log(API);
router.beforeEach(function (to, from, next) {
  var path = to.path;
  var login = localStorage.getItem('mtk');

  if (path === '/Login') {
    next();
    return;
  }

  if (login) {
    if (path === '/') {
      next({
        path: '/QueryIndex'
      });
    } else {
      next();
    }
  } else {
    next({
      path: '/Login'
    });
  }
});
new Vue({
  el: '#app',
  router: router,
  // 注入到根实例中
  i18n: i18n,
  render: function render(h) {
    return h(App);
  }
});
